import React from 'react'
import { useState, useEffect } from 'react'
import { useParams, Link } from 'react-router-dom'

const Edit = () => {
    const [updateProd, setUpProd] = useState([])

    useEffect(() => {
        fetch('http://127.0.0.1:9000/shop/all')
            .then((response) => response.json())
            .then((productsArray) => setUpProd(productsArray))
    }, [])

    return (
        <div>
            <div>
                {
                    updateProd.map((ele, i) =>
                        <div key={i} style={{ display: 'inline' }}>

                            <p>{updateProd.ProductName}</p>
                            {/* <img src={ele.ProductLink} alt="foto" style={{ width: '5%' }} /> */}
                        </div>
                    )
                }

            </div>

            <h2>EDIT PRODUCT</h2>
            <button >Update Product</button>
            <br />
            <button>Delete Product</button>
            <br />
            <input type="text" placeholder="Insert Product ID and hit delete" />
        </div >
    )
}


export default Edit
