import HomeCard from '../components/HomeCard'
const Home = () => {
    const [products, setProducts] = useState([])


    var arr = [];

    var maxVal = 4.5;
    var i;
    var delta = Math.floor(products.length / maxVal);
    {
        for (i = 0; i < products.length; i = i + delta) {
            arr.push(products[i]);
        }
    }


    useEffect(() => {
        fetch('http://127.0.0.1:8080/shop/all')
            .then((response) => response.json())
            .then((productsArray) => setProducts(productsArray))
    }, [])





    return (
        <div>
