            {
                arr.map((product, i) =>
                    <HomeCard
                        key={i}
                        ProductLink={product.ProductLink}
                        ProductName={product.ProductName}
                        Company={product.Company}
                        Price={product.Price}
                    />
                )
            }







        </div>

    )
}

export default Home
