import { useState } from "react";
import { useEffect } from "react";
import { useParams, useLocation } from "react-router-dom";



const Details = () => {
    const { id } = useParams();
    const [product, setProduct] = useState();


    //   Daten für Ausgabe ziehen
    const location = useLocation();
    let data = location.state;

    // console.log(id)
    useEffect(() => {
        fetch("http://localhost:9000/shop/details" + id)
            .then(response => response.json())
            .then(productObj => console.log(productObj))
    }, [id])
    if (product) return (
        <div>

            {data
                .filter((elt) => elt.id === uniqueId)
                .map((elt, index) => (
                    <div key={index} className="product_card_child_grid_detail">
                        <h1>{elt.category}</h1>
                        {console.log(`${elt.img}`)}
                        <img src={`/${elt.img}`} alt="" width="400" />
                        <h2>{elt.price}</h2>
                        <p>{elt.description}</p>
                    </div>
                ))}
            <h2>{product.ProductName}</h2>
        </div >
    )
    else return (<h1>Loading...</h1>)
}

export default Details
