import React from 'react'
import { useState, useEffect } from 'react'
import { useParams, Link } from 'react-router-dom'

const Edit = () => {
    const [products, setProducts] = useState([])

    useEffect(() => {
        fetch('http://127.0.0.1:9000/shop/all')
            .then((response) => response.json())
            .then((productsArray) => setProducts(productsArray))
    }, [])
    const { id } = useParams();
    const [product, setProduct] = useState([]);

    // console.log(id)
    useEffect(() => {
        fetch("http://localhost:9000/shop/login/edit/" + id)
            .then(response => response.json())
            .then(productObj => setProduct(productObj))
    }, [id])

    return (
        <div>
            <div>
                {
                    products.map((ele, i) =>
                        <div key={i} style={{ display: 'inline' }}>
                            <Link to={`/login/edit/${ele.id}`}>
                                <img src={ele.ProductLink} alt="foto" style={{ width: '5%' }} />
                            </Link>
                        </div>
                    )
                }
                <img src={product.ProductLink} alt="" />
                <h2>{product.ProductName}</h2>
                <p>{product.Company}</p>
                <h2>{product.Price}</h2>
                <h2>Product Description:</h2>
                <a href={product.LinkShop}>BUY PRODUCT</a>

            </div>

            <h2>EDIT PRODUCT</h2>
            <button >Update Product</button>
            <br />
            <button>Delete Product</button>
            <br />
            <input type="text" placeholder="Insert Product ID and hit delete" />
        </div >
    )
}


export default Edit
