import { useState, useEffect } from 'react'
import HomeCard from '../components/HomeCard'
const Home = () => {
    const [products, setProducts] = useState([])
    var arr = [];

    var maxVal = 4.5;

    var delta = Math.floor(obj.length / maxVal);

    // avoid filter because you don't want
    // to loop over 10000 elements !
    // just access them directly with a for loop !
    //                                 |
    //                                 V
    for (i = 0; i < obj.length; i = i + delta) {
        arr.push(obj[i]);
    }

    useEffect(() => {
        fetch('http://127.0.0.1:8080/shop/all')
            .then((response) => response.json())
            .then((productsArray) => setProducts(productsArray))
    }, [])





    return (
        <div>
            {
                products.map((product, i) =>
                    <HomeCard
                        key={i}
                        ProductLink={product.ProductLink}
                        ProductName={product.ProductName}
                        Company={product.Company}
                        Price={product.Price}
                    />
                )
            }

        </div>

    )
}

export default Home
