import React from 'react'
import { useState, useEffect } from 'react'
import { useParams } from 'react-router-dom'

const Edit = () => {


    const { id } = useParams();
    const [deleteById, setDeleteById] = useState(null);

    // console.log(id)

    useEffect(() => {
        const deleteProduct = {
            method: "put", headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(deleteProduct)

        }
        fetch("http://localhost:9000/shop/details/" + id, deleteProduct)
            .then(response => response.json())
            .then(productObj => setDeleteById(productObj))
    }, [])



    return (
        <div>
            <h2>EDIT PRODUCT</h2>
            <button >Update Product</button>
            <br />
            <button onClick={deleteById}>Delete Product</button>
            <br />
            <input type="text" placeholder="Insert Product ID and hit delete" />
        </div>
    )
}


export default Edit
