import { useState } from 'react'
import { Link } from 'react-router-dom'

const RandomProduct = () => {
    const [products, setProducts] = useState([])

    function getRandomProduct(products, num) {

        let shuffled = products.sort(() => 0.5 - Math.random());
        fetch('http://127.0.0.1:9000/shop/all')
            .then((response) => response.json())
            .then((shuffled) => setProducts(shuffled))

        return shuffled.slice(0, 10);
    }

    // console.log(getRandomProduct(products, 2)); // üëâÔ∏è ['a', 'c'];



    return (
        <div>
            {
                getRandomProduct(products).map((product, i) =>
                    <div key={i}>
                        <img src={product.ProductLink} alt="foto" />
                        <Link to={`/details/${product.id}`}>
                            <h2>{product.ProductName}</h2>
                        </Link>
                    </div>
                )
            }
            <button onClick={getRandomProduct}>Random Products</button>
        </div>
    )
}

export default RandomProduct
