import { useState, useEffect } from 'react'
import HomeCard from '../components/HomeCard'

const Home = () => {
    const [products, setProducts] = useState([])
    let everySeventh = [];
    const maxVal = 4.5;
    let delta = Math.floor(products.length / maxVal);
    {
        for (let i = 0; i < products.length; i = i + delta) {
            everySeventh.push(products[i]);
        }
    }
    // console.log(everySeventh)




    useEffect(() => {
        fetch('http://127.0.0.1:8080/shop/all')
            .then((response) => response.json())
            .then((productsArray) => setProducts(productsArray))
    }, [])


    return (
        <div>
            {/* {
                everySeventh.map((product, i) =>
                    <HomeCard
                        key={i}
                        ProductLink={product.ProductLink}
                        ProductName={product.ProductName}
                        Company={product.Company}
                        Price={product.Price}
                    />
                )
            } */}
            {

                products.filter(e => everySeventh).map((product, i) =>
                    <HomeCard
                        key={i}
                        ProductLink={product.ProductLink}
                        ProductName={product.ProductName}
                        Company={product.Company}
                        Price={product.Price}
                    />
                )



            }

        </div>
    )
}

export default Home
